import React from 'react';
import './RewardCard.css';

const ImageRewardCard = ({ imageUrl, description, title, onClick }) => {
    return (
        <div className="card reward-card image-reward-card" onClick={onClick}>
            {/* Construct the full URL by prepending the backend API URL */}
            {imageUrl && (
                <div className="image-container">
                    <img src={`${process.env.REACT_APP_API_URL}${imageUrl}`} alt={description || 'Reward Image'} className="reward-image" />
                    {title && <span className="image-title-overlay">{title}</span>}
                </div>
            )}
            {description && (
                <p className="reward-description">{description}</p>
            )}
        </div>
    );
};

export default ImageRewardCard;
