import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const CreateQuizPage = () => {
    const navigate = useNavigate();
    const [title, setTitle] = useState('');
    const [scoreGoal, setScoreGoal] = useState(1);
    const [anniversaryDate, setAnniversaryDate] = useState('');
    const [congratulationsMessage, setCongratulationsMessage] = useState('');
    const [questions, setQuestions] = useState([{ question: '', answer: '' }]);
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(false);

    const handleQuestionChange = (index, event) => {
        const newQuestions = [...questions];
        newQuestions[index][event.target.name] = event.target.value;
        setQuestions(newQuestions);
    };

    const addQuestion = () => {
        setQuestions([...questions, { question: '', answer: '' }]);
    };

    const removeQuestion = (index) => {
        const newQuestions = questions.filter((_, i) => i !== index);
        setQuestions(newQuestions);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        setError('');

        const quizData = {
            title,
            scoreGoal,
            anniversaryDate,
            congratulationsMessage,
            questions: questions.filter(q => q.question && q.answer),
        };

        try {
            const response = await fetch('/api/quiz', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(quizData),
            });

            if (!response.ok) {
                const errData = await response.json();
                throw new Error(errData.message || 'Failed to create quiz.');
            }

            const { quizId } = await response.json();
            navigate(`/quiz/${quizId}`);

        } catch (err) {
            setError(err.message);
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="main-wrapper">
            <div className="card">
                <h1 className="text-3xl font-bold text-center mb-8">ðŸŽ¯ Create Your Custom Quiz</h1>
                <form onSubmit={handleSubmit} noValidate>
                    <div className="form-group">
                        <label htmlFor="title" className="block text-sm font-semibold mb-2">Quiz Title</label>
                        <input id="title" type="text" placeholder="e.g., Our Story" value={title} onChange={e => setTitle(e.target.value)} required />
                    </div>

                    <div className="form-group">
                        <label htmlFor="scoreGoal" className="block text-sm font-semibold mb-2">Score to Win</label>
                        <input id="scoreGoal" type="number" value={scoreGoal} onChange={e => setScoreGoal(e.target.value)} min="1" required />
                    </div>

                    <div className="form-group">
                        <label htmlFor="anniversaryDate" className="block text-sm font-semibold mb-2">Special Date</label>
                        <input id="anniversaryDate" type="date" value={anniversaryDate} onChange={e => setAnniversaryDate(e.target.value)} required />
                    </div>

                    <div className="form-group">
                        <label htmlFor="congratulationsMessage" className="block text-sm font-semibold mb-2">Custom "Congratulations" Message (Optional)</label>
                        <textarea id="congratulationsMessage" placeholder="e.g., You're the best!" value={congratulationsMessage} onChange={e => setCongratulationsMessage(e.target.value)} />
                    </div>

                    <div className="form-section">
                        {questions.map((q, index) => (
                            <div key={index} className="question-row">
                                <div className="form-group" style={{flex: 1}}>
                                    <label htmlFor={`question-${index}`} className="block text-sm font-semibold mb-2">Question {index + 1}</label>
                                    <input id={`question-${index}`} name="question" type="text" placeholder="Enter your question" value={q.question} onChange={e => handleQuestionChange(index, e)} required />
                                </div>
                                <div className="form-group" style={{flex: 1}}>
                                    <label htmlFor={`answer-${index}`} className="block text-sm font-semibold mb-2">Answer</label>
                                    <input id={`answer-${index}`} name="answer" type="text" placeholder="Enter the answer" value={q.answer} onChange={e => handleQuestionChange(index, e)} required />
                                </div>
                                <button type="button" onClick={() => removeQuestion(index)} className="remove-question-btn" aria-label="Remove question">
                                    âœ– Remove
                                </button>
                            </div>
                        ))}
                        <div style={{textAlign: 'center'}}>
                            <button type="button" onClick={addQuestion} className="secondary-button">
                                âž• Add Another Question
                            </button>
                        </div>
                    </div>

                    <button type="submit" disabled={loading} className="interactive-button">
                        {loading ? 'Creating...' : 'Create & Get Link'}
                    </button>
                    {error && <p style={{ color: 'red', textAlign: 'center' }}>{error}</p>}
                </form>
            </div>
        </div>
    );
};

export default CreateQuizPage;
