import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const CreateQuizPage = () => {
    const navigate = useNavigate();
    const [title, setTitle] = useState('');
    const [scoreGoal, setScoreGoal] = useState(1);
    const [anniversaryDate, setAnniversaryDate] = useState('');
    const [congratulationsMessage, setCongratulationsMessage] = useState('');
    const [questions, setQuestions] = useState([{ question: '', answer: '' }]);
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(false);

    const handleQuestionChange = (index, event) => {
        const newQuestions = [...questions];
        newQuestions[index][event.target.name] = event.target.value;
        setQuestions(newQuestions);
    };

    const addQuestion = () => {
        setQuestions([...questions, { question: '', answer: '' }]);
    };

    const removeQuestion = (index) => {
        const newQuestions = questions.filter((_, i) => i !== index);
        setQuestions(newQuestions);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        setError('');

        const quizData = {
            title,
            scoreGoal,
            anniversaryDate,
            congratulationsMessage,
            questions: questions.filter(q => q.question && q.answer),
        };

        try {
            const response = await fetch('/api/quiz', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(quizData),
            });

            if (!response.ok) {
                const errData = await response.json();
                throw new Error(errData.message || 'Failed to create quiz.');
            }

            const { quizId } = await response.json();
            navigate(`/quiz/${quizId}`);

        } catch (err) {
            setError(err.message);
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="main-wrapper">
            <div className="card">
                <h1>Create Your Custom Quiz</h1>
                <form onSubmit={handleSubmit} noValidate>
                    <input type="text" placeholder="Quiz Title (e.g., Our Story)" value={title} onChange={e => setTitle(e.target.value)} required />
                    <input type="number" placeholder="Score to Win" value={scoreGoal} onChange={e => setScoreGoal(e.target.value)} min="1" required />
                    <input type="date" placeholder="Special Date" value={anniversaryDate} onChange={e => setAnniversaryDate(e.target.value)} required />
                    <textarea placeholder="Custom 'Congratulations' Message (Optional)" value={congratulationsMessage} onChange={e => setCongratulationsMessage(e.target.value)} />

                    <hr />
                    <h3>Questions</h3>
                    {questions.map((q, index) => (
                        <div key={index} className="question-row">
                            <input
                                name="question"
                                type="text"
                                placeholder={`Question ${index + 1}`}
                                value={q.question}
                                onChange={e => handleQuestionChange(index, e)}
                                required />
                            <input
                                name="answer"
                                type="text"
                                placeholder="Answer"
                                value={q.answer}
                                onChange={e => handleQuestionChange(index, e)}
                                required />
                            <button type="button" onClick={() => removeQuestion(index)} className="remove-question-btn" aria-label="Remove question">&times;</button>
                        </div>
                    ))}
                    <button type="button" onClick={addQuestion} className="secondary-button">Add Another Question</button>
                    <button type="submit" disabled={loading} className="interactive-button">
                        {loading ? 'Creating...' : 'Create & Get Link'}
                    </button>
                    {error && <p style={{ color: 'red', textAlign: 'center' }}>{error}</p>}
                </form>
            </div>
        </div>
    );
};

export default CreateQuizPage;
