import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';

const ProfilePage = () => {
    const [quizzes, setQuizzes] = useState([]);
    const [loading, setLoading] = useState(true);
    const { token } = useAuth();

    useEffect(() => {
        const fetchQuizzes = async () => {
            try {
                const response = await fetch(`${process.env.REACT_APP_API_URL}/api/quiz/my-quizzes`, {
                    headers: { 'x-auth-token': token }
                });
                if (!response.ok) throw new Error('Failed to fetch quizzes');
                const data = await response.json();
                setQuizzes(data);
            } catch (error) {
                console.error(error);
            } finally {
                setLoading(false);
            }
        };

        if (token) {
            fetchQuizzes();
        }
    }, [token]);

    if (loading) {
        return <div className="main-wrapper"><div className="card"><h1>Loading Your Quizzes...</h1></div></div>;
    }

    return (
        <div className="main-wrapper">
            <div className="card">
                <h1>My Quizzes</h1>
                {quizzes.length > 0 ? (
                    <ul className="quiz-list">
                        {quizzes.map(quiz => (
                            <li key={quiz._id} className="quiz-list-item">
                                <Link to={`/quiz/${quiz.quizId}`}>{quiz.title}</Link>
                                <span className="quiz-date">Created on: {new Date(quiz.createdAt).toLocaleDateString()}</span>
                            </li>
                        ))}
                    </ul>
                ) : (
                    <p>You haven't created any quizzes yet. <Link to="/create">Create one now!</Link></p>
                )}
            </div>
        </div>
    );
};

export default ProfilePage;
