import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';

const LoginPage = () => {
    const navigate = useNavigate();
    const { login } = useAuth();
    const [formData, setFormData] = useState({ email: '', password: '' });
    const [error, setError] = useState('');
    const { email, password } = formData;

    const onChange = e => setFormData({ ...formData, [e.target.name]: e.target.value });

    const onSubmit = async e => {
        e.preventDefault();
        setError('');
        try {
            const response = await fetch(`${process.env.REACT_APP_API_URL}/api/users/login`, {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(formData),
            });

            const data = await response.json();

            if (!response.ok) {
                // Assuming the backend sends errors in a specific format
                const errorMsg = data.errors ? data.errors[0].msg : 'Login failed. Please check your credentials.';
                throw new Error(errorMsg);
            }

            login(data.token);
            navigate('/create'); // Redirect to create page after successful login

        } catch (err) {
            setError(err.message);
        }
    };

    return (
        <div className="main-wrapper">
            <div className="card">
                <h1 className="form-title">Sign In</h1>
                <form onSubmit={onSubmit} noValidate>
                    <div className="form-group">
                        <label htmlFor="email">Email Address</label>
                        <input type="email" name="email" value={email} onChange={onChange} required />
                    </div>
                    <div className="form-group">
                        <label htmlFor="password">Password</label>
                        <input type="password" name="password" value={password} onChange={onChange} required />
                    </div>
                    {error && <p className="error-message">{error}</p>}
                    <button type="submit" className="interactive-button">Login</button>
                </form>
                <p className="form-footer-text">
                    Don't have an account? <Link to="/register">Sign Up</Link>
                </p>
            </div>
        </div>
    );
};

export default LoginPage;
