import React, { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';

const PrivateRoute = ({ children }) => {
    const { isAuthenticated, setAuthModalOpen } = useAuth();
    const navigate = useNavigate();

    useEffect(() => {
        if (!isAuthenticated) {
            // If user is not authenticated, redirect to home and then open the login modal.
            // This prevents the user from being on a blank /create page.
            navigate('/');
            setAuthModalOpen(true, 'login');
        }
    }, [isAuthenticated, setAuthModalOpen, navigate]);

    // Render children if authenticated, otherwise render null (or a loading spinner)
    return isAuthenticated ? children : null;
};

export default PrivateRoute;
