import React, { createContext, useState, useContext } from 'react';

const AuthContext = createContext();

export const useAuth = () => useContext(AuthContext);

export const AuthProvider = ({ children }) => {
    // Check for a token in localStorage to initialize the state
    const [token, setToken] = useState(localStorage.getItem('token'));
    const [isAuthModalOpen, setIsAuthModalOpen] = useState(false);
    const [authModalView, setAuthModalView] = useState('login'); // 'login' or 'register'

    const login = (newToken) => {
        localStorage.setItem('token', newToken);
        setToken(newToken);
    };

    const logout = () => {
        localStorage.removeItem('token');
        setToken(null);
    };

    const setAuthModalOpen = (isOpen, view = 'login') => {
        if (isOpen) {
            setAuthModalView(view);
        }
        setIsAuthModalOpen(isOpen);
    };

    // The !!token expression converts the token string (or null) into a boolean
    const isAuthenticated = !!token;

    const value = {
        token,
        isAuthenticated,
        isAuthModalOpen,
        authModalView,
        setAuthModalOpen,
        login,
        logout,
    };

    return <AuthContext.Provider value={value}>{children}</AuthContext.Provider>;
};
