import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import './AuthModal.css';

const AuthModal = () => {
    const { authModalView, setAuthModalOpen, login } = useAuth();
    const navigate = useNavigate();

    const [view, setView] = useState(authModalView);
    const [animation, setAnimation] = useState('entering');

    const [loginData, setLoginData] = useState({ email: '', password: '' });
    const [registerData, setRegisterData] = useState({ name: '', email: '', password: '' });
    const [error, setError] = useState('');
    const modalBodyRef = useRef(null);

    useEffect(() => {
        setView(authModalView);
        setAnimation('entering');
        const timer = setTimeout(() => setAnimation(''), 300); // Match animation duration
        return () => clearTimeout(timer);
    }, [authModalView]);

    const switchView = (newView) => {
        const modalBody = modalBodyRef.current;
        if (!modalBody) return;

        // 1. Set a fixed height to prevent collapsing
        modalBody.style.height = `${modalBody.offsetHeight}px`;

        // 2. Start the exit animation
        setAnimation('exiting');

        // 3. After a short delay, switch content and animate height
        setTimeout(() => {
            setView(newView);
            setError(''); // Clear errors when switching views

            // Measure the height of the new content *before* it's visible
            const newContent = modalBody.querySelector('.form-container');
            if (newContent) {
                modalBody.style.height = `${newContent.offsetHeight}px`;
            }

            setAnimation('entering');
            // 4. After animation, restore auto height
            setTimeout(() => { modalBody.style.height = 'auto'; }, 300);
        }, 200); // Start the switch slightly before the exit animation finishes
    };

    const handleLoginChange = e => setLoginData({ ...loginData, [e.target.name]: e.target.value });
    const handleRegisterChange = e => setRegisterData({ ...registerData, [e.target.name]: e.target.value });

    const handleLoginSubmit = async e => {
        e.preventDefault();
        setError('');
        try {
            const response = await fetch(`${process.env.REACT_APP_API_URL}/api/users/login`, {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(loginData),
            });
            const data = await response.json();
            if (!response.ok) throw new Error(data.errors ? data.errors[0].msg : 'Login failed.');
            login(data.token);
            setAuthModalOpen(false);
            navigate('/create');
        } catch (err) {
            setError(err.message);
        }
    };

    const handleRegisterSubmit = async e => {
        e.preventDefault();
        setError('');
        try {
            const response = await fetch(`${process.env.REACT_APP_API_URL}/api/users/register`, {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(registerData),
            });
            const data = await response.json();
            if (!response.ok) throw new Error(data.errors ? data.errors[0].msg : 'Registration failed.');
            login(data.token);
            setAuthModalOpen(false);
            navigate('/create');
        } catch (err) {
            setError(err.message);
        }
    };

    const loginForm = (
        <div className={`form-container ${view === 'login' ? animation : 'exiting'}`}>
            <h1 className="form-title">Sign In</h1>
            <form onSubmit={handleLoginSubmit} noValidate>
                <div className="form-group">
                    <label htmlFor="login-email">Email Address</label>
                    <input id="login-email" type="email" name="email" value={loginData.email} onChange={handleLoginChange} required />
                </div>
                <div className="form-group">
                    <label htmlFor="login-password">Password</label>
                    <input id="login-password" type="password" name="password" value={loginData.password} onChange={handleLoginChange} required />
                </div>
                {error && <p className="error-message">{error}</p>}
                <button type="submit" className="interactive-button">Login</button>
            </form>
            <p className="form-footer-text">
                Don't have an account? <button onClick={() => switchView('register')} className="link-button">Sign Up</button>
            </p>
        </div>
    );

    const registerForm = (
        <div className={`form-container ${view === 'register' ? animation : 'exiting'}`}>
            <h1 className="form-title">Sign Up</h1>
            <form onSubmit={handleRegisterSubmit} noValidate>
                <div className="form-group">
                    <label htmlFor="register-name">Name</label>
                    <input id="register-name" type="text" name="name" value={registerData.name} onChange={handleRegisterChange} required />
                </div>
                <div className="form-group">
                    <label htmlFor="register-email">Email Address</label>
                    <input id="register-email" type="email" name="email" value={registerData.email} onChange={handleRegisterChange} required />
                </div>
                <div className="form-group">
                    <label htmlFor="register-password">Password</label>
                    <input id="register-password" type="password" name="password" value={registerData.password} onChange={handleRegisterChange} required />
                </div>
                {error && <p className="error-message">{error}</p>}
                <button type="submit" className="primary-button">Register</button>
            </form>
            <p className="form-footer-text">
                Already have an account? <button onClick={() => switchView('login')} className="link-button">Sign In</button>
            </p>
        </div>
    );

    return (
        <div className="modal-overlay" onClick={() => setAuthModalOpen(false)}>
            <div className="auth-modal-content" onClick={e => e.stopPropagation()}>
                <button className="modal-close-icon" onClick={() => setAuthModalOpen(false)} aria-label="Close modal">&times;</button>
                <div ref={modalBodyRef} className="auth-modal-body">
                    {/* Render both forms to allow for height measurement, but only one is visible */}
                    <div style={{ opacity: view === 'login' ? 1 : 0, position: view === 'login' ? 'relative' : 'absolute' }}>{loginForm}</div>
                    <div style={{ opacity: view === 'register' ? 1 : 0, position: view === 'register' ? 'relative' : 'absolute' }}>{registerForm}</div>
                </div>
            </div>
        </div>
    );
};

export default AuthModal;
