import React, { useState, useEffect } from 'react';
import ThemeToggle from './ThemeToggle';
import { init as initConfetti } from '../utils/canvasConfetti.js';

const Layout = ({ children }) => {
    const [colorMode, setColorMode] = useState('dark');

    useEffect(() => {
        // Initialize the confetti canvas as soon as the main layout mounts
        const canvas = document.getElementById('confetti-canvas');
        if (canvas) {
            initConfetti(canvas);
        }
        document.body.className = colorMode === 'light' ? 'light-mode' : '';
    }, [colorMode]);

    const toggleColorMode = () => {
        setColorMode(prev => (prev === 'light' ? 'dark' : 'light'));
    };

    return (
        <>
            <canvas id="confetti-canvas"></canvas>
            {children}
            <footer className="footer">
                <span>ChanLoii © 2025 • Created by Kenneth</span>
                <ThemeToggle colorMode={colorMode} toggleColorMode={toggleColorMode} />
            </footer>
        </>
    );
};

export default Layout;
