import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import QuoteCard from '../components/QuoteCard.js';
import DateDetailsCard from '../components/DateDetailsCard.js';
import QuizModal from '../components/QuizModal.js';
import ThemeToggle from '../components/ThemeToggle.js';
import { init as initConfetti } from '../utils/canvasConfetti.js';

const SpecialMessage = ({ message }) => (
    <div className="special-message">{message}</div>
);

const QuizPage = () => {
    const { quizId } = useParams();
    const [quizData, setQuizData] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    const [isDetailsVisible, setDetailsVisible] = useState(false);
    const [isQuizOpen, setQuizOpen] = useState(false);
    const [specialOccasion, setSpecialOccasion] = useState(null);
    const [colorMode, setColorMode] = useState('light');

    useEffect(() => {
        initConfetti(document.getElementById('confetti-canvas'));

        const fetchQuizData = async () => {
            try {
                setLoading(true);
                const response = await fetch(`/api/quiz/${quizId}`);
                if (!response.ok) {
                    throw new Error('Quiz not found. Maybe the link is old or incorrect?');
                }
                const data = await response.json();
                setQuizData(data);
            } catch (err) {
                setError(err.message);
            } finally {
                setLoading(false);
            }
        };

        fetchQuizData();
    }, [quizId]);

    useEffect(() => {
        if (!quizData) return;

        const checkSpecialOccasions = () => {
            // TODO: Implement special occasions logic based on quizData.anniversaryDate
        };

        checkSpecialOccasions();

        if (specialOccasion) {
            document.body.className = specialOccasion.name;
        } else {
            document.body.className = colorMode === 'light' ? 'light-mode' : '';
        }
    }, [quizData, specialOccasion, colorMode]);

    const handleQuizSuccess = () => {
        setQuizOpen(false);
        setDetailsVisible(true);
        setTimeout(() => setDetailsVisible(false), 60000);
    };

    const toggleColorMode = () => {
        setColorMode(prev => (prev === 'light' ? 'dark' : 'light'));
    };

    if (loading) {
        return <div className="main-wrapper"><div className="card"><h1>Loading Quiz...</h1></div></div>;
    }

    if (error) {
        return <div className="main-wrapper"><div className="card"><h1>Error</h1><p>{error}</p><Link to="/" className="interactive-button">Go Home</Link></div></div>;
        return <div className="main-wrapper"><div className="card"><h1>Error</h1><p style={{ margin: '20px 0' }}>{error}</p><Link to="/" className="interactive-button">Go Home</Link></div></div>;
    }

    return (
        <div className="main-wrapper">
            <canvas id="confetti-canvas"></canvas>
            {specialOccasion && <SpecialMessage message={specialOccasion.message} />}
            <QuoteCard />

            {isDetailsVisible && <DateDetailsCard anniversaryDate={quizData.anniversaryDate} />}

            {!isDetailsVisible && (
                <button className="interactive-button" onClick={() => setQuizOpen(true)} disabled={!quizData}>
                    {quizData.title || "Unlock Our Story"} ðŸ’–
                </button>
            )}

            {isQuizOpen && quizData && (
                <QuizModal
                    gameData={quizData}
                    onSuccess={handleQuizSuccess}
                    onClose={() => setQuizOpen(false)}
                />
            )}

            <footer className="footer">
                <span>ChanLoii Â© 2025 â€¢ Created by Kenneth</span>
                <ThemeToggle
                    colorMode={colorMode}
                    toggleColorMode={toggleColorMode}
                    isSpecialOccasion={!!specialOccasion}
                />
            </footer>
        </div>
    );
};

export default QuizPage;
