import React, { useEffect } from 'react';
import { useAuth } from '../context/AuthContext';

const PrivateRoute = ({ children }) => {
    const { isAuthenticated, setAuthModalOpen } = useAuth();

    useEffect(() => {
        if (!isAuthenticated) {
            // If user is not authenticated, open the login modal
            setAuthModalOpen(true, 'login');
        }
    }, [isAuthenticated, setAuthModalOpen]);

    // Render children if authenticated, otherwise render null (or a loading spinner)
    return isAuthenticated ? children : null;
};

export default PrivateRoute;
