import React from 'react';
import './RewardCard.css';

const ImageRewardCard = ({ imageUrl, description }) => {
    return (
        <div className="card reward-card image-reward-card">
            <img src={imageUrl} alt={description || 'Reward Image'} className="reward-image" />
            {description && (
                <p className="reward-description">{description}</p>
            )}
        </div>
    );
};

export default ImageRewardCard;
